#include <iostream>
#include <queue>
using namespace std;
int m, n, x1, y1, x2, y2, ans = -1;
char a[305][305];
bool v[305][305][4];
const int dx[] = {-1, 0, 1, 0},
dy[] = {0, 1, 0, -1};
struct Node {
	int x, y, t;
	Node(int a, int b, int c)
	: x(a), y(b), t(c) {
	}
};
queue<Node> q;
void bfs() {
	q.push(Node(x1, y1, 0));
	v[x1][y1][0] = v[x1][y1][1] =v[x1][y1][2] =v[x1][y1][3] = true;
	while (!q.empty()) {
		Node f = q.front();
		for (int i = 0; i < 4; i++) {
			int nx = f.x + dx[i],
			ny = f.y + dy[i];
			if (1 <= nx && nx <= m
			&& 1 <= ny && ny <= n
			&& a[nx][ny] != 'R'
			&& a[nx][ny] != 'S'
			&& !v[nx][ny][i]) {
				int nt = f.t +
				(a[nx][ny] == 'B' ? 2 : 1);
				if (nx == x2 && ny == y2) {
					if (ans == -1 ||
					ans > nt) ans = nt;
					break;
				}
				q.push(Node(nx, ny, nt));
				v[nx][ny][i] = true;
//				cout << nx << ' '
//				<< ny << ' '
//				<< nt << ' '
//				<< ' ' << endl;
			}
		}
		q.pop();
	}
}
int main() {
	cin >> m >> n;
	for (int i = 1; i <= m; i++) {
		for (int j = 1; j <= n; j++) {
			cin >> a[i][j];
			if (a[i][j] == 'Y') {
				x1 = i, y1 = j;
			} else if (a[i][j] == 'T') {
				x2 = i, y2 = j;
			}
		}
	}
	bfs();
	cout << ans;
	return 0;
}
